import React from "react";
import { HashRouter as Router, Switch, Route } from "react-router-dom";
import About from "./components/About";
import Work from "./components/Work";
import Contact from "./components/Contact";

class Main extends React.Component {
  render() {
    const App = () => (
      <Router>
        <Route
          render={({ location, ...rest }) => (
            <div className="main-wrapper">
              <div className="main">
                <Transition
                  native
                  config={{ tension: 1, friction: 10 }}
                  keys={location.pathname.split("/").filter(a => a)[0]}
                  from={{ transform: "translateY(500px)", opacity: 0 }}
                  enter={{ transform: "translateY(0px)", opacity: 1 }}
                  leave={{ transform: "translateY(500px)", opacity: 0 }}
                >
                  {style => (
                    <Switch location={location}>
                      <Route
                        path="/about"
                        render={props => About({ ...props, style })}
                      />
                      <Route
                        path="/work"
                        render={props => Work({ ...props, style })}
                      />
                      <Route
                        path="/contact"
                        render={props => Contact({ ...props, style })}
                      />
                      <Route render={() => <div>Not Found</div>} />
                    </Switch>
                  )}
                </Transition>
                <div className="copy-container">
                  <h1>Shuuhei Igarashi</h1>
                  <p>Welcome to my web site</p>
                </div>
              </div>
            </div>
          )}
        />
      </Router>
    );

    return (
      <div className="main-wrapper">
        <div className="main"></div>
      </div>
    );
  }
}

export default Main;
